import { NavLink } from "react-router-dom";
import { FaSearch } from "react-icons/fa";
import { InputGroup, Form } from "react-bootstrap";
import styled from "styled-components";

import "./home.css";
const Container = styled.div`
  display: flex;
  margin: 20px;
  flex-direction: column;
`;


const MiniContainer = styled.div`
  display: flex;
  flex
`
const Right = styled.div`
  flex: 1;
  border: 1px solid red;
  height: 60vh;
`;
const Left = styled.div`
  flex: 1;
  border: 1px solid red;
`;

const FeaturedImg = styled.div`
  height: 100%;
  background-image: url(https://a.c-dn.net/b/4dKtJi/GettyImages-155140239+resized.jpg);
`;
const Home = () => {
  return (
    <div>
      <div className="Header">
        <div className="Logo">
          <p>MarketFx</p>
        </div>
        <div className="func">
          <div className="search">
            <InputGroup className="mb-3">
              <Form.Control
                placeholder="Recipient's username"
                aria-label="Recipient's username"
                aria-describedby="basic-addon2"
              />
              <InputGroup.Text id="basic-addon2">
                <FaSearch />{" "}
              </InputGroup.Text>
            </InputGroup>
          </div>
          <div className="logbuttons">
            <button className="SignUp btn btn-primary mr-4">SignUp</button>
            <button className="Login btn btn-outline-primary ml-5">
              Login
            </button>
          </div>
        </div>
      </div>
      <nav></nav>

      <Container>
        <h2>Market News</h2>
        <MiniContainer>
          <Left>
            <FeaturedImg />
          </Left>
          <Right></Right>
        </MiniContainer>
      </Container>
    </div>
  );
};
export default Home;
